mapper.delete:
update PC_RELIABLE_INITTASK
set DEL_FLAG = 1
where ID = #{id,jdbcType=VARCHAR}

mapper.sql:
WHERE PC_RELIABLE_INITTASK.DEL_FLAG != 1

    AND TRIAL_TASK_ID = #{trialTaskId}


    AND PC_RELIABLE_INITTASK.TASK_NUMBER = #{taskNumber}


    AND TASK_NAME LIKE '%${taskName}%'


    AND INIT_CODE LIKE '%initCode%'


    AND TASK_TYPE = #{taskType}


    AND CREATE_TIME >= #{createDate1}


    AND CREATE_TIME <= #{createDate2}
